window.JavascriptConsole.find_pos = function(e) {
    var curtop = 0;
    var curleft = 0;
    var obj = e.target ? e.target : e.srcElement;

    if (obj && obj.offsetParent) {

    	do {
	    curleft += obj.offsetLeft;
	    curtop += obj.offsetTop;

	} while (obj = obj.offsetParent);
    }

    return [curleft,curtop];

}

