function prime_squares_sum(a, b) {
    function plus(x, y) {
        return x + y;
    }
    return filtered_accumulate(plus, 0,
                               square, a, inc, b, 
                               is_prime);
}
function relative_prime_product(n) {    
    function times(x, y) {
        return x * y;
    }
    function identity(x) {
        return x;
    }
    function test(i) {
        return gcd(i, n) === 1;
    }
    return filtered_accumulate(times, 1,
                               identity, 1, inc, n,
                               test);
}

prime_squares_sum(4, 10); // 74
