(define (sum-odd-squares tree) (cond ((null? tree) 0) ((not (pair? tree)) (if (odd? tree) (square tree) 0)) (else (+ (sum-odd-squares (car tree)) (sum-odd-squares (cdr tree))))))
function sum_odd_squares(tree) { return is_null(tree) ? 0 : ! is_pair(tree) ? (is_odd(tree) ? square(tree) : 0) : sum_odd_squares(head(tree)) + sum_odd_squares(tail(tree)); }
