function split(identity_op, smaller_op) {
    function rec_split(painter, n) {
        if (n===0) {
            return painter;
        } else {
            const smaller = rec_split(painter, n - 1);
            return identity_op(painter, 
                        smaller_op(smaller, smaller));
        }
    }
    return rec_split;
}






