SUBSECTION.TEXT:
    We can also create objects that handle
    deposit message for bank account
    deposits as well as
    withdrawals, and thus we can represent simple bank accounts.  Here is
    a

      procedure
      function

    that returns a bank-account object with a specified initial
    balance:

      make_account 
      make_account
      make_account_example_my

(define (make-account balance)
   (define (withdraw amount)
      (if (>= balance amount)
         (begin (set! balance (- balance amount))
                balance)
            "Insufficient funds"))
   (define (deposit amount)
      (set! balance (+ balance amount))
      balance)
   (define (dispatch m)
      (cond ((eq? m 'withdraw) withdraw)
            ((eq? m 'deposit) deposit)
            (else (error "Unknown request - - MAKE-ACCOUNT"
                   m))))
   dispatch)


function make_account(balance) {
    function withdraw(amount) {
        if (balance >= amount) {
            balance = balance - amount;
            return balance;
        } else {
            return "Insufficient funds";
        }
    }
    function deposit(amount) {
        balance = balance + amount;
        return balance;
    }
    function dispatch(m) {
        return m === "withdraw"
               ? withdraw
               : m === "deposit"
               ? deposit
               : error(m, "unknown request -- make_account");
    }
    return dispatch;
}



      make_account_example_my

            (define acc (make-account 100))
            ((acc 'withdraw) 50)


const acc = make_account(100);

acc("withdraw")(50);

