            (define (mystery x)
            (define (loop x y)
            (if (null? x)
            y
            (let ((temp (cdr x)))
            (set-cdr! x y)
            (loop temp x))))
            (loop x '()))


function mystery(x) {
    function loop(x, y) {
        if (is_null(x)) {
            return y;
        } else {
            let temp = tail(x);
            set_tail(x, y);
            return loop(temp, x);
        }
    }
    return loop(x, null);
}



