                (define (make-wire)
                (let ((signal-value 0) (action-procedures '()))
                (define (set-my-signal! new-value)
                (if (not (= signal-value new-value))
                (begin (set! signal-value new-value)
                (call-each action-procedures))
                'done))

                (define (accept-action-procedure! proc)
                (set! action-procedures (cons proc action-procedures))
                (proc))

                (define (dispatch m)
                (cond ((eq? m 'get-signal) signal-value)
                ((eq? m 'set-signal!) set-my-signal!)
                ((eq? m 'add-action!) accept-action-procedure!)
                (else (error "Unknown operation -- WIRE" m))))
                dispatch))


function make_wire() {
    let signal_value = 0;
    let action_functions = null;
    function set_my_signal(new_value) {
        if (signal_value !== new_value) {
            signal_value = new_value;
            call_each(action_functions);
        } else {}
    }
    function accept_action_function(fun) {
        action_functions = pair(fun, action_functions);
        fun();
    }
    function dispatch(m) {
        return m === "get_signal"
               ? signal_value
               : m === "set_signal"
                 ? set_my_signal
                 : m === "add_action"
                   ? accept_action_function
                   : Error("Unknown operation in wire", m);
    }
    return dispatch;
}

        The local

	  procedure
	  function


          set-my-signal
          set_my_signal


	tests whether the new signal value changes the signal on the wire.
	If so, it runs each of the action
        proceduresfunctions, using the following
        procedurefunction
        call-eachcall_each,
        which calls each of the items in a list of no-argument
        proceduresfunctions:




                (define (call-each procedures)
                (if (null? procedures)
                'done
                (begin
                ((car procedures))
                (call-each (cdr procedures)))))


function call_each(functions) {
    if (is_null(functions)) {
        return "done";
    } else {
        (head(functions))();
        call_each(tail(functions));
    }
}

        The simulation is driven by the
        procedurefunction
        propagate, which
        operates on 
            the-agenda
            the_agenda
        , executing each
        procedurefunction
        on the agenda
        in sequence.  In general, as the simulation runs, new items will be
        added to the agenda, and propagate will continue the simulation
        as long as there are items on the agenda:




                (define (propagate)
                (if (empty-agenda? the-agenda)
                'done
                (let ((first-item (first-agenda-item the-agenda)))
                (first-item)
                (remove-first-agenda-item! the-agenda)
                (propagate))))


function propagate() {
    if (is_empty_agenda(the_agenda)) {
        return "done";
    } else {
        const first = first_agenda_item(the_agenda);
        first();
        remove_first_agenda_item(the_agenda);
        return propagate();
    }
}


