      scale_stream 
      scale_stream
      scale_stream_example
      6

(define (scale-stream stream factor)
  (stream-map (lambda (x) (* x factor)) stream))


function scale_stream(stream, factor) {
    return stream_map(x => x * factor,
                      stream);
}


