	Literal values are parsed into tagged lists with
	tag "literal" and
	the actual value.

	  \begin{eqnarray*}
	  \ll \textit{literal-value} \gg & = &
	  \texttt{list("literal", }\textit{value}\texttt{)}
	  \end{eqnarray*}

	where $\textit{value}$ is
	the JavaScript value represented by the
	$\textit{literal-value}$ string.
	Here $\ll \textit{literal-value} \gg$ denotes the
	result of parsing the string $\textit{literal-value}$.

	  parse_literal_example

parse("1;");


list("literal", 1)



	  parse_literal_example_2

parse("'hello world';");


list("literal", "hello world")



	  parse_literal_example_3

parse("null;");


list("literal", null)


	The syntax predicate is 
	is_literal.

	  is_literal
	  is_literal_example
	  true

function is_literal(component) {
    is_tagged_list(component, "literal");
}


function is_literal(component) {
    return is_number(component)  ||
           is_string(component)  ||
           is_boolean(component) ||
           is_null(component)    ||
           is_undefined(component);
}
function literal_value(component) {    
    return component;
}



