;; preloaded:
;; (begin 
;;   (define (append xs y) 
;;       (if (null? xs) 
;;           y 
;;           (cons (car xs) (append (cdr xs) y)))) 
;;   (append (cons 1 \'()) (cons 2 \'())))

    (begin (define (append xs y) (if (null? xs) y (cons (car xs) (append (cdr xs) y)))) (append (cons 1 \'()) (cons 2 \'())))


function append(xs,ys) { 
    if (is_null(xs)) {
       return ys; 
    } else {
       return pair(head(xs),append(tail(xs),ys)); 
    }
} 
append(pair(1,null),pair(2,null));
