      driver_loop_amb_example

(define input-prompt ";;; Amb-Eval input:")
(define output-prompt ";;; Amb-Eval value:")

(define (driver-loop)
  (define (internal-loop try-again)
    (prompt-for-input input-prompt)
    (let ((input (read)))
      (if (eq? input 'try-again)
          (try-again)
          (begin
            (newline)
            (display ";;; Starting a new problem ")
            (ambeval input
                     the-global-environment
                     ;; ambeval success
                     (lambda (val next-alternative)
                       (announce-output output-prompt)
                       (user-print val)
                       (internal-loop next-alternative))
                    ;; ambeval failure
                    (lambda ()
                      (announce-output
                       ";;; There are no more values of")
                      (user-print input)
                      (driver-loop)))))))
  (internal-loop
   (lambda ()
     (newline)
     (display ";;; There is no current problem")
     (driver-loop))))


const input_prompt = "amb-evaluate input:";
const output_prompt =  "amb-evaluate value:";

function driver_loop(env) {
    function internal_loop(retry) {
        const input = user_read(input_prompt);
        if (input === "retry") {
            return retry();
        } else {
            display("Starting a new problem");
            const program = parse(input);
            const locals = scan_out_declarations(program);
            const unassigneds = list_of_unassigned(locals);
            const program_env = extend_environment(locals, unassigneds, env);
            return ambeval(
                       program,  
                       program_env,
                       // ambeval success
                       (val, next_alternative) => {
                           user_print(output_prompt, val);
                           return internal_loop(next_alternative);
                       },
                       // ambeval failure
                       () => {
                           display("There are no more values of");
                           display(input);
                           return driver_loop(env);
                });
        }
    }
    return internal_loop( () => {
                              display("There is no current problem");
                              return driver_loop(env);
                          } );
}


const input_prompt = "amb-evaluate input:";
const output_prompt =  "amb-evaluate value:";
function driver_loop() {
    function internal_loop(retry) {
        const input = user_read(input_prompt);
        if (is_null(input)) {
        display("--- evaluator terminated ---", "");
        } else if (input === "retry") {
            display("----------------------------",
                    input_prompt + "\n" + input + "\n");
            return retry();
        } else {
            display("--- starting new problem ---",
                    input_prompt + "\n" + input + "\n");
            ambeval(parse("{ " + input + " }"),
                the_global_environment,
                // ambeval success
                (val, next_alternative) => {
                    user_print(output_prompt, val);
                    return internal_loop(next_alternative);
                },
                // ambeval failure
                () => {
                    display("----------------------------",
                            "no more values of:\n" + input + "\n");
                    return driver_loop();
                });
        }
    }
    return internal_loop(
               () => {
                   display("---  no current problem  ---", "");
                   return driver_loop();
               });
}


    The initial call to

      internal-loop
      internal_loop

    uses a

      try-again
      retry


      procedure
      function

    that complains that there is no current problem and restarts the driver loop.
    This is the behavior that will happen if the user types

      try-again

	retry


    when there is no evaluation in progress.

