function first_answer(input) {
    const exp = parse(input + ";");
    const q = query_syntax_process(exp);
    const frames = evaluate_query(q, singleton_stream(null));
    return is_null(frames)
           ? "no matching data"
           : unparse(instantiate_expression(exp, head(frames)));
}


