fib_recursive_controller fib_recursive_example 8
(controller (assign continue (label fib-done)) fib-loop (test (op <) (reg n) (const 2)) (branch (label immediate-answer)) ;; set up to compute ^${\textrm{Fib}}(n-1)$^ (save continue) (assign continue (label afterfib-n-1)) (save n) ; save old value of ^$n$^ (assign n (op -) (reg n) (const 1)) ; clobber ^$n$^ to ^$n-1$^ (goto (label fib-loop)) ; perform recursive call afterfib-n-1 ; upon return, ^val^ contains ^${\textrm{Fib}}(n-1)$^ (restore n) (restore continue) ;; set up to compute ^${\textrm{Fib}}(n-2)$^ (assign n (op -) (reg n) (const 2)) (save continue) (assign continue (label afterfib-n-2)) (save val) ; save ^${\textrm{Fib}}(n-1)$^ (goto (label fib-loop)) afterfib-n-2 ; upon return, ^val^ contains ^${\textrm{Fib}}(n-2)$^ (assign n (reg val)) ; ^$n$^ now contains ^${\textrm{Fib}}(n-2)$^ (restore val) ; ^val^ now contains ^${\textrm{Fib}}(n-1)$^ (restore continue) (assign val ; ^${\textrm{Fib}}(n-1)+{\textrm{Fib}}(n-2)$^ (op +) (reg val) (reg n)) (goto (reg continue)) ; return to caller, answer is in ^val^ immediate-answer (assign val (reg n)) ; base case: ^${\textrm{Fib}}(n)=n$^ (goto (reg continue)) fib-done)
controller( list( assign("continue", label("fib_done")), "fib_loop", test(list(op("<"), reg("n"), constant(2))), branch(label("immediate_answer")), // set up to compute Fib(n-1) save("continue"), assign("continue", label("afterfib_n_1")), save("n"), // save old value of n assign("n", list(op("-"), reg("n"), constant(1))), // clobber n to n - 1 go_to(label("fib_loop")), // perform recursive call "afterfib_n_1", // upon return, "val" contains Fib(n-1) restore("n"), restore("continue"), // set up to compute Fib(n-2) assign("n", list(op("-"), reg("n"), constant(2))), save("continue"), assign("continue", label("afterfib_n_2")), save("val"), // save Fib(n-1) go_to(label("fib_loop")), "afterfib_n_2", // upon return, val is Fib(n-2) assign("n", reg("val")), // n now contains Fib(n-2) restore("val"), // val now contains Fib(n-1) restore("continue"), assign("val", // Fib(n-1) + Fib(n-2) list(op("+"), reg("val"), reg("n"))), go_to(reg("continue")), // return to caller, answer in val "immediate_answer", assign("val", reg("n")), // base case: Fib(n) = n go_to(reg("continue")), "fib_done"))
fib_recursive_example controller make_machine start fib_recursive_declaration
;
const fib_recursive_machine = make_machine( list("n", "val", "continue"), list(list("<", (a, b) => a < b), list("-", (a, b) => a - b), list("+", (a, b) => a + b)), controller_sequence(fib_recursive_controller));
set_register_contents(fib_recursive_machine, "n", 6); start(fib_recursive_machine); get_register_contents(fib_recursive_machine, "val");
