    We can also represent a stack as a

      procedure
      function

    with local state.  The

      procedure
      function


      make-stack
      make_stack

    creates a stack whose local state consists
    of a list of the items on the stack.  A stack accepts requests to
    push an item onto the stack, to
    pop the top item off the stack
    and return it, and to
    initialize the stack to empty.

      make_stack 
      make_stack
      gcd_machine_complete_example

(define (make-stack)
  (let ((s '()))
    (define (push x)
      (set! s (cons x s)))
    (define (pop)
      (if (null? s)
          (error "Empty stack - - POP")
          (let ((top (car s)))
            (set! s (cdr s))
            top)))
    (define (initialize)
      (set! s '())
      'done)
    (define (dispatch message)
      (cond ((eq? message 'push) push)
            ((eq? message 'pop) (pop))
            ((eq? message 'initialize) (initialize))
            (else (error "Unknown request - - STACK"
                         message))))
    dispatch))


function make_stack() {
    let stack = null;
    let frame = null;
    function push_marker() {
        frame = pair(stack, frame);
        return "done";
    }
    function pop_marker() {
        stack = head(frame);
        frame = tail(frame);
        return "done";
    }
    function push(x) { 
        stack = pair(x, stack); 
        return "done";
    }
    function pop() {
        if (is_null(stack)) {
            error("empty stack -- pop");
        } else {
            const top = head(stack);
            stack = tail(stack);
            return top;
        }
    }
    function initialize() {
        stack = null;
        return "done";
    }
    function dispatch(message) {
        return message === "push"
               ? push
               : message === "pop"
               ? pop()
               : message === "push_marker"
               ? push_marker()
               : message === "pop_marker"
               ? pop_marker()
               : message === "initialize"
               ? initialize()
               : error(message, "unknown request -- stack");
    }
    return dispatch;
}

function make_push_marker_to_stack_ef(machine, stack, pc) {
    return () => {
                   push_marker(stack);
                   advance_pc(pc);
                 };
}
function make_revert_stack_to_marker_ef(machine, stack, pc) {
    return () => {
                   pop_marker(stack);
                   advance_pc(pc); 
                 };
}

function push_marker_to_stack() { return list("push_marker_to_stack"); }
function revert_stack_to_marker() { return list("revert_stack_to_marker"); }

function pop_marker(stack) {
    return stack("pop_marker");
}
function push_marker(stack) {
    return stack("push_marker");
}




