    The final instruction type, handled by
    perform (in register machine)simulating

      make-perform,

      make_perform_ef,

    generates an execution

      procedure
      function

    for the action to be performed.  At simulation time, the action

      procedure
      function

    is executed and the pc advanced.

      make_perform_ef 
      make_perform
      is_register_exp
      gcd_machine_complete_example

(define (make-perform inst machine labels operations pc)
  (let ((action (perform-action inst)))
    (if (operation-exp? action)
        (let ((action-proc
               (make-operation-exp
                action machine labels operations)))
          (lambda ()
            (action-proc)
            (advance-pc pc)))
        (error "Bad PERFORM instruction - - ASSEMBLE" inst))))

(define (perform-action inst) (cdr inst))


function make_perform_ef(inst, machine, labels, operations, pc) {
    const action = perform_action(inst);
    if (is_operation_exp(action)) {
        const action_fun = make_operation_exp_ef(action, machine,
                                                 labels, operations);
        return () => { 
                       action_fun(); 
                       advance_pc(pc); 
                     };
    } else {
        error(inst, "bad perform instruction -- assemble");
    }
}





